// src/pages/FormSubmitterPage.jsx
import React, { useState, useEffect, useRef } from "react";
import toast, { Toaster } from "react-hot-toast";
import axios from "axios";

const MAX_FILE_SIZE_MB = 10;

// ---- Base URL (no change in behavior) ----
const API_BASE = (() => {
  const cra = (typeof process !== "undefined" && process.env && process.env.REACT_APP_API_BASE) || "";
  const win = (typeof window !== "undefined" && window.__API_BASE__) || "";
  let base = cra || win;
  if (!base) {
    const { protocol, hostname, port } = window.location;
    base =
      port === "3000"
        ? `${protocol}//${hostname}:8000`
        : `${protocol}//${hostname}${port ? ":" + port : ""}`;
  }
  return base.replace(/\/$/, "");
})();

function getAccessToken() {
  const keys = ["access_token", "token", "authToken", "accessToken", "id_token", "jwt", "jwt_token"];
  for (const k of keys) {
    const v = localStorage.getItem(k) || sessionStorage.getItem(k);
    if (v) return v;
  }
  const m = document.cookie.match(/(?:^|;\s*)(access_token|token)=([^;]+)/i);
  if (m) return decodeURIComponent(m[2]);
  return "";
}

export default function FormSubmitterPage() {
  const [csvFile, setCsvFile] = useState(null);
  const [proxy, setProxy] = useState("");
  const [haltOnCaptcha, setHaltOnCaptcha] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [lastJobId, setLastJobId] = useState(null);

  const fileInputRef = useRef(null);

  useEffect(() => {
    document.title = "Campaign Submission Center | CPS";
  }, []);

  const pickFile = () => fileInputRef.current?.click();

  const validateCsv = (file) => {
    if (!file) return "Please choose a CSV file.";
    if (!file.name.toLowerCase().endsWith(".csv")) return "Please select a .csv file.";
    if (file.size > MAX_FILE_SIZE_MB * 1024 * 1024)
      return `File exceeds ${MAX_FILE_SIZE_MB}MB limit.`;
    return "";
  };

  const handleFileChange = (e) => {
    const file = e.target.files?.[0];
    const err = validateCsv(file);
    if (err) return toast.error(err);
    setCsvFile(file);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const file = e.dataTransfer.files?.[0];
    const err = validateCsv(file);
    if (err) return toast.error(err);
    setCsvFile(file);
  };

  const clearFile = (e) => {
    e.preventDefault();
    setCsvFile(null);
    if (fileInputRef.current) fileInputRef.current.value = "";
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!csvFile) return toast.error("A CSV file is required.");

    const token = getAccessToken();
    if (!token) return toast.error("You must be logged in. No access token found.");

    const formData = new FormData();
    formData.append("file", csvFile);
    formData.append("proxy", proxy || "");
    formData.append("haltOnCaptcha", haltOnCaptcha ? "true" : "false");

    try {
      setSubmitting(true);
      toast.success("🚀 Submission started!");

      // Expect backend to return { job_id, campaign_id?, message }
      const { data } = await axios.post(`${API_BASE}/submit/start`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          Authorization: `Bearer ${token}`,
        },
        timeout: 0,
      });

      const jobId = data?.job_id || data?.campaign_id || data?.id;
      if (jobId) {
        setLastJobId(jobId);
        toast.success(`✅ Submission triggered (Job: ${jobId})`);
      } else {
        toast.success("✅ Submission triggered!");
      }
    } catch (err) {
      console.error(err);
      const msg = err?.response?.data?.detail || err?.message || "Error starting submission.";
      toast.error(`❌ ${msg}`);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="relative min-h-screen text-gray-100 overflow-hidden">
      {/* Grand gradient background */}
      <div className="pointer-events-none absolute inset-0 bg-[radial-gradient(1000px_400px_at_70%_-10%,rgba(99,102,241,0.25),transparent),radial-gradient(800px_300px_at_-10%_10%,rgba(236,72,153,0.18),transparent),linear-gradient(180deg,#0b1020_0%,#090d1b_60%,#070b16_100%)]" />
      {/* Soft grid */}
      <div className="pointer-events-none absolute inset-0 bg-[linear-gradient(transparent,transparent_23px,rgba(255,255,255,0.05)_24px),linear-gradient(90deg,transparent,transparent_23px,rgba(255,255,255,0.05)_24px)] bg-[size:24px_24px]" />

      <Toaster position="top-right" />
      <div className="relative max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <header className="mb-10">
          <div className="inline-flex items-center gap-2 rounded-full px-3 py-1 bg-white/10 border border-white/20 text-xs text-indigo-200">
            Automated Outreach
          </div>
          <h1 className="mt-4 text-4xl sm:text-5xl font-extrabold tracking-tight">
            Campaign <span className="text-transparent bg-clip-text bg-gradient-to-r from-indigo-300 via-pink-300 to-sky-300">Submission Center</span>
          </h1>
          <p className="text-gray-300 mt-3 max-w-2xl">
            Upload a CSV and launch your automated form submissions. We store a clean, human-friendly log for each site.
          </p>
        </header>

        <div className="rounded-3xl border border-white/10 bg-white/[0.06] backdrop-blur-sm shadow-2xl shadow-indigo-900/20">
          <div className="p-6 sm:p-10">
            <form onSubmit={handleSubmit} className="space-y-10">
              {/* CSV upload */}
              <section>
                <div className="flex items-center justify-between mb-2">
                  <label className="text-base font-semibold">1. Upload CSV File</label>
                  {csvFile && (
                    <button
                      onClick={clearFile}
                      className="text-sm text-rose-300 hover:text-rose-200 underline underline-offset-4"
                      type="button"
                    >
                      Remove
                    </button>
                  )}
                </div>

                <div
                  className="group relative border-2 border-dashed rounded-2xl p-8 sm:p-10 bg-white/[0.04] hover:bg-white/[0.07] border-white/20 transition-colors cursor-pointer"
                  onClick={pickFile}
                  onDragOver={(e) => e.preventDefault()}
                  onDrop={handleDrop}
                >
                  <div className="flex flex-col items-center text-center">
                    <svg
                      className="w-14 h-14 text-indigo-300/80 group-hover:text-indigo-200"
                      fill="none"
                      stroke="currentColor"
                      viewBox="0 0 24 24"
                    >
                      <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth="1.5"
                        d="M7 16a4 4 0 01-4-4V7a4 4 0 014-4h1.6A4 4 0 0115.4 7L12 10.4"
                      />
                    </svg>

                    {csvFile ? (
                      <div className="mt-4">
                        <p className="font-semibold text-indigo-200">{csvFile.name}</p>
                        <p className="text-xs text-gray-400">
                          {(csvFile.size / 1024).toFixed(2)} KB
                        </p>
                      </div>
                    ) : (
                      <div className="mt-4">
                        <p className="text-gray-200">Drag & drop your .csv here</p>
                        <p className="text-sm text-gray-400">
                          or <span className="font-semibold text-indigo-300">browse</span> to upload
                        </p>
                      </div>
                    )}

                    <input
                      ref={fileInputRef}
                      type="file"
                      accept=".csv"
                      className="sr-only"
                      onChange={handleFileChange}
                    />
                  </div>

                  {!csvFile && (
                    <p className="absolute bottom-3 right-4 text-[11px] text-gray-500">
                      Max size: {MAX_FILE_SIZE_MB}MB
                    </p>
                  )}
                </div>
              </section>

              {/* Proxy & Captcha */}
              <section className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label htmlFor="proxy" className="block text-base font-semibold mb-1.5">
                    2. Proxy (Optional)
                  </label>
                  <input
                    id="proxy"
                    type="text"
                    value={proxy}
                    onChange={(e) => setProxy(e.target.value)}
                    placeholder="http://user:pass@ip:port"
                    className="w-full px-3 py-3 rounded-xl bg-white/[0.08] border border-white/20 outline-none focus:ring-2 focus:ring-indigo-400/30 focus:border-indigo-400/40 placeholder:text-gray-500"
                  />
                  <p className="text-xs text-gray-400 mt-1">
                    Leave blank to use the default outbound connection.
                  </p>
                </div>

                <div>
                  <label className="block text-base font-semibold mb-1.5">
                    3. CAPTCHA Handling
                  </label>

                  <div className="flex items-center justify-between px-4 py-3 rounded-xl bg-white/[0.08] border border-white/20">
                    <span className="text-sm text-gray-200">Halt on CAPTCHA</span>

                    <label className="relative inline-flex items-center cursor-pointer">
                      <input
                        type="checkbox"
                        className="sr-only peer"
                        checked={haltOnCaptcha}
                        onChange={() => setHaltOnCaptcha(!haltOnCaptcha)}
                      />
                      <span className="w-11 h-6 rounded-full bg-white/20 peer-checked:bg-indigo-500 transition-colors"></span>
                      <span className="absolute left-1 top-1 w-4 h-4 rounded-full bg-white transform transition-transform peer-checked:translate-x-5"></span>
                    </label>
                  </div>
                  <p className="text-xs text-gray-400 mt-1">
                    When enabled, the run pauses if a CAPTCHA is encountered.
                  </p>
                </div>
              </section>

              {/* Submit */}
              <div className="pt-2 flex flex-col sm:flex-row gap-3">
                <button
                  type="submit"
                  disabled={submitting}
                  className={`inline-flex items-center justify-center gap-2 px-5 py-3.5 rounded-2xl font-semibold
                    ${submitting ? "bg-indigo-800/60 cursor-not-allowed" : "bg-indigo-600 hover:bg-indigo-500"}
                    text-white shadow-xl shadow-indigo-900/30 transition w-full sm:w-auto`}
                >
                  {submitting ? (
                    <>
                      <svg
                        className="animate-spin h-5 w-5"
                        viewBox="0 0 24 24"
                        fill="none"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
                        <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v4a4 4 0 00-4 4H4z" />
                      </svg>
                      Processing…
                    </>
                  ) : (
                    <>Start Submitting</>
                  )}
                </button>

                <a
                  href={lastJobId ? `/logs?job_id=${encodeURIComponent(lastJobId)}` : "/logs"}
                  className="inline-flex items-center justify-center gap-2 px-5 py-3.5 rounded-2xl font-semibold bg-white/[0.08] hover:bg-white/[0.12] border border-white/20 transition w-full sm:w-auto text-indigo-200"
                >
                  View Recent Logs {lastJobId ? `(Job ${lastJobId})` : ""}
                </a>
              </div>

              {lastJobId && (
                <p className="text-xs text-gray-400 mt-2">
                  Tip: Monitor clean, user-friendly logs on the Logs page. Technical noise is hidden by default.
                </p>
              )}
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
