// src/pages/LogsPage.jsx
import React, { useEffect, useState } from "react";
import toast, { Toaster } from "react-hot-toast";
import axios from "axios";
import { useLocation } from "react-router-dom"; // ✅ use router hook

// -------- API Base Resolver --------
const API_BASE = (() => {
  const cra = (typeof process !== "undefined" && process.env && process.env.REACT_APP_API_BASE) || "";
  const win = (typeof window !== "undefined" && window.__API_BASE__) || "";
  let base = cra || win;
  if (!base) {
    const { protocol, hostname, port } = window.location;
    base = port === "3000" ? `${protocol}//${hostname}:8000` : `${protocol}//${hostname}${port ? ":" + port : ""}`;
  }
  return base.replace(/\/$/, "");
})();

function getToken() {
  return (
    localStorage.getItem("access_token") ||
    localStorage.getItem("token") ||
    sessionStorage.getItem("access_token") ||
    sessionStorage.getItem("token") ||
    ""
  );
}

export default function LogsPage() {
  const loc = useLocation(); // ✅ React Router location
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(false);
  const [jobId, setJobId] = useState("");
  const [campaignId, setCampaignId] = useState("");
  const [level, setLevel] = useState("");
  const [limit, setLimit] = useState(100);
  const [offset, setOffset] = useState(0);
  const [total, setTotal] = useState(null);

  // pick job_id from URL on mount and when URL changes
  useEffect(() => {
    const q = new URLSearchParams(loc.search);
    setJobId(q.get("job_id") || "");
  }, [loc.search]);

  useEffect(() => {
    document.title = "Recent Logs | CPS";
  }, []);

  const fetchLogs = async () => {
    try {
      setLoading(true);
      const token = getToken();
      const params = { limit, offset };
      if (jobId) params.job_id = jobId;
      if (campaignId) params.campaign_id = campaignId;
      if (level) params.level = level;

      const { data } = await axios.get(`${API_BASE}/logs`, {
        headers: token ? { Authorization: `Bearer ${token}` } : {},
        params,
      });

      setRows(data?.items || data || []);
      setTotal(data?.total ?? null);
    } catch (e) {
      console.error(e);
      toast.error("Failed to fetch logs");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchLogs();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [limit, offset, jobId, campaignId, level]);

  const nextPage = () => setOffset((v) => v + limit);
  const prevPage = () => setOffset((v) => Math.max(0, v - limit));

  return (
    <div className="bg-[#0b1020] min-h-screen text-gray-100">
      <Toaster position="top-right" />
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 py-10">
        <div className="mb-8 flex items-center justify-between">
          <div>
            <h1 className="text-2xl sm:text-3xl font-bold">Recent Logs</h1>
            <p className="text-gray-400 mt-1">
              Logs are stored in the database. Use filters to narrow results.
            </p>
          </div>
          <a
            href="/"
            className="inline-flex items-center px-4 py-2 rounded-lg bg-white/[0.06] border border-white/15 hover:bg-white/[0.1]"
          >
            ← Back to Submitter
          </a>
        </div>

        {/* Filters */}
        <div className="grid grid-cols-1 md:grid-cols-5 gap-4 mb-6">
          <input
            value={jobId}
            onChange={(e) => {
              setJobId(e.target.value);
              setOffset(0);
            }}
            placeholder="Job ID"
            className="px-3 py-2 rounded-lg bg-white/[0.06] border border-white/15 outline-none"
          />
          <input
            value={campaignId}
            onChange={(e) => {
              setCampaignId(e.target.value);
              setOffset(0);
            }}
            placeholder="Campaign ID"
            className="px-3 py-2 rounded-lg bg-white/[0.06] border border-white/15 outline-none"
          />
          <select
            value={level}
            onChange={(e) => {
              setLevel(e.target.value);
              setOffset(0);
            }}
            className="px-3 py-2 rounded-lg bg-white/[0.06] border border-white/15 outline-none"
          >
            <option value="">Any Level</option>
            <option value="DEBUG">DEBUG</option>
            <option value="INFO">INFO</option>
            <option value="WARN">WARN</option>
            <option value="ERROR">ERROR</option>
          </select>
          <select
            value={limit}
            onChange={(e) => {
              setLimit(parseInt(e.target.value, 10));
              setOffset(0);
            }}
            className="px-3 py-2 rounded-lg bg-white/[0.06] border border-white/15 outline-none"
          >
            {[25, 50, 100, 200].map((n) => (
              <option key={n} value={n}>
                {n} per page
              </option>
            ))}
          </select>
          <button
            onClick={() => {
              setOffset(0);
              fetchLogs();
            }}
            className="px-4 py-2 rounded-lg bg-indigo-600 hover:bg-indigo-500 text-white font-semibold"
          >
            Apply Filters
          </button>
        </div>

        {/* Table */}
        <div className="rounded-xl overflow-hidden border border-white/10 shadow-xl">
          <div className="bg-[#0f1530] px-4 py-3 text-sm text-gray-300 flex items-center justify-between">
            <span>
              {loading ? "Loading…" : `Showing ${rows.length}${total ? ` of ${total}` : ""}`}
            </span>
            <div className="space-x-2">
              <button
                disabled={offset === 0 || loading}
                onClick={prevPage}
                className="px-3 py-1 rounded-md bg-white/[0.06] border border-white/15 hover:bg-white/[0.1] disabled:opacity-50"
              >
                Prev
              </button>
              <button
                disabled={loading || (total !== null && offset + limit >= total)}
                onClick={nextPage}
                className="px-3 py-1 rounded-md bg-white/[0.06] border border-white/15 hover:bg-white/[0.1] disabled:opacity-50"
              >
                Next
              </button>
            </div>
          </div>

          <div className="overflow-x-auto bg-[#0a0f24]">
            <table className="min-w-full text-sm">
              <thead className="text-gray-300 bg-white/[0.03] border-b border-white/10">
                <tr>
                  <th className="px-4 py-2 text-left">Time</th>
                  <th className="px-4 py-2 text-left">Level</th>
                  <th className="px-4 py-2 text-left">Job</th>
                  <th className="px-4 py-2 text-left">Campaign</th>
                  <th className="px-4 py-2 text-left">Message</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-white/5">
                {rows.map((r) => (
                  <tr key={r.id}>
                    <td className="px-4 py-2 text-gray-400">{r.ts}</td>
                    <td className="px-4 py-2">
                      <span
                        className={`px-2 py-0.5 rounded-md text-xs ${
                          r.level === "ERROR"
                            ? "bg-red-500/20 text-red-300"
                            : r.level === "WARN"
                            ? "bg-yellow-500/20 text-yellow-200"
                            : r.level === "DEBUG"
                            ? "bg-slate-500/20 text-slate-300"
                            : "bg-emerald-500/20 text-emerald-300"
                        }`}
                      >
                        {r.level}
                      </span>
                    </td>
                    <td className="px-4 py-2 text-gray-300">{r.job_id || "-"}</td>
                    <td className="px-4 py-2 text-gray-300">{r.campaign_id || "-"}</td>
                    <td className="px-4 py-2 text-gray-200">{r.message}</td>
                  </tr>
                ))}
                {rows.length === 0 && !loading && (
                  <tr>
                    <td className="px-4 py-6 text-gray-500" colSpan={5}>
                      No logs found for the selected filters.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}
