// src/App.jsx
import React, { useState } from "react";
import { Routes, Route, Navigate, useNavigate } from "react-router-dom";
import { Toaster } from "react-hot-toast";

import AppLayout from "./components/layout/AppLayout";
import LandingPage from "./pages/LandingPage";
import DashboardPage from "./pages/DashboardPage";
import CampaignsPage from "./pages/CampaignsPage";
import CampaignDetailPage from "./pages/CampaignDetailPage";
import UserProfileForm from "./pages/UserProfileForm";
import AdminDashboard from "./pages/AdminDashboard";
import OwnerDashboard from "./pages/OwnerDashboard";
import UserDashboard from "./pages/UserDashboard";
import FormSubmitterPage from "./pages/FormSubmitterPage";

import PasswordResetForm from "./components/PasswordResetForm"; // single form handles both flows

import useAuth from "./hooks/useAuth";
import AuthModal from "./components/AuthModal";

// ---------- Route Guards ----------
const ProtectedRoute = ({ children }) => {
  const { user, loading } = useAuth();
  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen text-lg">
        Loading...
      </div>
    );
  }
  return user ? children : <Navigate to="/" replace />;
};

const AdminRoute = ({ children }) => {
  const { user, loading } = useAuth();
  if (loading) return null;
  return user && (user.role === "admin" || user.role === "owner")
    ? children
    : <Navigate to="/dashboard" replace />;
};

const OwnerRoute = ({ children }) => {
  const { user, loading } = useAuth();
  if (loading) return null;
  return user?.role === "owner" ? children : <Navigate to="/dashboard" replace />;
};

const UserRoute = ({ children }) => {
  const { user, loading } = useAuth();
  if (loading) return null;
  return user?.role === "user" ? children : <Navigate to="/dashboard" replace />;
};

// ---------- App ----------
const App = () => {
  const { loading } = useAuth();
  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate();

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen text-lg">
        Loading...
      </div>
    );
  }

  const openAuth = () => setShowModal(true);

  return (
    <>
      <Toaster
        position="top-center"
        toastOptions={{
          style: {
            padding: "12px 20px",
            fontSize: "15px",
            background: "#1f2937",
            color: "#fff",
            borderRadius: "8px",
            boxShadow: "0 8px 25px rgba(0,0,0,0.2)",
          },
          success: { iconTheme: { primary: "#10b981", secondary: "#ffffff" } },
          error:   { iconTheme: { primary: "#ef4444", secondary: "#ffffff" } },
        }}
      />

      <Routes>
        {/* Layout wraps everything so header/footer appear everywhere */}
        <Route element={<AppLayout onLogin={openAuth} onRegister={openAuth} />}>
          {/* Public landing */}
          <Route
            index
            element={
              <LandingPage
                onLogin={openAuth}
                onRegister={openAuth}
              />
            }
          />

          {/* Authenticated */}
          <Route
            path="/dashboard"
            element={
              <ProtectedRoute>
                <DashboardPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/campaigns"
            element={
              <ProtectedRoute>
                <CampaignsPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/campaigns/:campaignId"
            element={
              <ProtectedRoute>
                <CampaignDetailPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/profile"          // ✅ corrected route (was /UserProfileForm)
            element={
              <ProtectedRoute>
                <UserProfileForm />
              </ProtectedRoute>
            }
          />
          <Route
            path="/form-submitter"
            element={
              <ProtectedRoute>
                <FormSubmitterPage />
              </ProtectedRoute>
            }
          />

          {/* Role-based */}
          <Route
            path="/admin"
            element={
              <AdminRoute>
                <AdminDashboard />
              </AdminRoute>
            }
          />
          <Route
            path="/owner"
            element={
              <OwnerRoute>
                <OwnerDashboard />
              </OwnerRoute>
            }
          />
          <Route
            path="/user"
            element={
              <UserRoute>
                <UserDashboard />
              </UserRoute>
            }
          />

          {/* Password reset (mount same form for both paths; let it branch via query/props) */}
          <Route path="/forgot-password" element={<PasswordResetForm mode="request" />} />
          <Route path="/reset-password" element={<PasswordResetForm mode="reset" />} />

          {/* Catch-all */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Route>
      </Routes>

      {/* Auth Modal (login/register) */}
      <AuthModal isOpen={showModal} onClose={() => setShowModal(false)} />
    </>
  );
};

export default App;
